};

export default connect(mapStateToProps, {
  editGroupStageSetting,
  editFinalStageSetting,
  editCompetition,
  queryAllFormats,
  queryCompetition,
  queryTournamentInfo,
  queryAllSports,
  querySportInfo,
  queryFinalStageSetting,
  queryGroupStageSetting,
  queryAllCompetitionsByTournamentId,
  updateSchedule,
  reportViolation,
  registTeam
})(CompetitionInfo);

